    // }else if (role === 4) {
    //     sidebarComponent = <sidebarSiswa />;
    // }  else {
        sidebarComponent = <sidebarAdmin />;
    // }
  return (
    <React.Fragment>
        <Navbars/>
        <div className="columns mt-6">
            <div className="column is-2">
                {sidebarComponent}
            </div>
            <column className="has-background-light">
                <main>{children}</main>
            </column>
        </div>
    </React.Fragment>
  )
}

export default Layout
